CREATE TABLE Treatment (ID BIGINT NOT NULL, TTYPE VARCHAR(31), DIAGNOSIS VARCHAR(255), patient_fk BIGINT, provider_fk BIGINT, PRIMARY KEY (ID))
CREATE TABLE DRUGTREATMENT (ID BIGINT NOT NULL, DOSAGE FLOAT, DRUG VARCHAR(255), PHYSICIAN_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Patient (ID BIGINT NOT NULL, BIRTHDATE DATE, NAME VARCHAR(255), PATIENTID BIGINT, PRIMARY KEY (ID))
CREATE TABLE PROVIDER (ID BIGINT NOT NULL, NAME VARCHAR(255), PATIENTDAO BYTEA, PROVIDERID BIGINT, SPECIALIZATION VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE RADIOLOGYTREATMENT (ID BIGINT NOT NULL, DATES BYTEA, RADIOLOGIST_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE SURGERYTREATMENT (ID BIGINT NOT NULL, S_DATE DATE, SURGEON_ID BIGINT, PRIMARY KEY (ID))
ALTER TABLE Treatment ADD CONSTRAINT FK_Treatment_patient_fk FOREIGN KEY (patient_fk) REFERENCES Patient (ID)
ALTER TABLE Treatment ADD CONSTRAINT FK_Treatment_provider_fk FOREIGN KEY (provider_fk) REFERENCES PROVIDER (ID)
ALTER TABLE DRUGTREATMENT ADD CONSTRAINT FK_DRUGTREATMENT_PHYSICIAN_ID FOREIGN KEY (PHYSICIAN_ID) REFERENCES PROVIDER (ID)
ALTER TABLE DRUGTREATMENT ADD CONSTRAINT FK_DRUGTREATMENT_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
ALTER TABLE RADIOLOGYTREATMENT ADD CONSTRAINT FK_RADIOLOGYTREATMENT_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
ALTER TABLE RADIOLOGYTREATMENT ADD CONSTRAINT FK_RADIOLOGYTREATMENT_RADIOLOGIST_ID FOREIGN KEY (RADIOLOGIST_ID) REFERENCES PROVIDER (ID)
ALTER TABLE SURGERYTREATMENT ADD CONSTRAINT FK_SURGERYTREATMENT_SURGEON_ID FOREIGN KEY (SURGEON_ID) REFERENCES PROVIDER (ID)
ALTER TABLE SURGERYTREATMENT ADD CONSTRAINT FK_SURGERYTREATMENT_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
